import {useParams, Link, Outlet, useNavigate} from 'react-router-dom'
import React, {Fragment, useState, useEffect} from 'react'
import {Dialog, Disclosure, Menu, Transition} from '@headlessui/react'
import {XMarkIcon} from '@heroicons/react/24/outline'
import Cookies from 'js-cookie';
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css'
import CategoryTabs from './categoryTabs.jsx'
import Products from './products.jsx'


function Cards() {
  const [subCategories, setCategories] = useState(null);
  const [tabs, setTabs] = useState([]);
  const [products, setProducts] = useState(null);
  const [loading, setLoading] = useState(true);

  const searchParams = new URLSearchParams(window.location.search);
  const rawData = searchParams.get('data');
  const decodedData = JSON.parse(decodeURIComponent(rawData));

  const [currentCategoryId, setCurrentCategoryId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
      const apiUrl = `http://127.0.0.1:8000/api/categories/`;

      fetch(apiUrl)
          .then((response) => {
              if (!response.ok) {
                  throw new Error('Сетевая ошибка');
              }
              return response.json();
          })
          .then((data) => {
              setCategories(data);
          })
          .catch((error) => {
              console.error('!!! Ошибка', error);
          });
  }, []);

  useEffect(() => {
      if (subCategories) {
          const categoryTabs = subCategories.map((category) => ({
              id: category.id,
              name: category.name,
              onClick: () => handleCategoryClick(category.id),
              current: category.id === currentCategoryId,
          }));
          setTabs(categoryTabs);
      }
  }, [subCategories, currentCategoryId]);

  // Updated handleCategoryClick
  const handleCategoryClick = (categoryId) => {
      const category_id = categoryId != null ? categoryId : ''; // If categoryId is null, use an empty string
      navigate(`/products?data=${encodeURIComponent(JSON.stringify({ category_id }))}`);
      fetchProducts(categoryId);
      setCurrentCategoryId(categoryId);
  };

// Updated fetchProducts
const fetchProducts = (categoryId) => {
const apiUrl = categoryId
  ? `http://127.0.0.1:8000/api/products/?category_id=${categoryId}`
  : 'http://127.0.0.1:8000/api/products/';

fetch(apiUrl)
  .then((response) => {
      if (!response.ok) {
          throw new Error('Network response was not ok');
      }
      return response.json();
  })
  .then((data) => {
      setProducts(data.results);
      setLoading(false);
  })
  .catch((error) => {
      console.error('Error fetching product:', error);
      setLoading(false);
  });
};


  // ... (other imports and component code)

useEffect(() => {
  const apiUrl = decodedData != null
  ? 
    decodedData.category_id != undefined ? `http://127.0.0.1:8000/api/products/?category_id=${decodedData.category_id}`
    : `http://127.0.0.1:8000/api/products/?category_id=${decodedData.parent_category_id}`
      : 'http://127.0.0.1:8000/api/products/';

    alert(decodedData.category_id)

  fetch(apiUrl)
      .then((response) => {
          if (!response.ok) {
              throw new Error('Network response was not ok');
          }
          return response.json();
      })
      .then((data) => {
          setProducts(data);
          setLoading(false);
      })
      .catch((error) => {
          console.error('Error fetching product:', error);
          setLoading(false);
      });
}, [window.location.search]);




return (
<>
    <CategoryTabs decodedData={decodedData} tabs={tabs}/>
    <Products products={products} />
</>
)
}


export default Cards;
