import { Routes, Route, useNavigate } from 'react-router-dom';
import React, {Fragment, useState, useEffect} from 'react'
import {Dialog, Disclosure, Menu, Transition} from '@headlessui/react'
import {XMarkIcon} from '@heroicons/react/24/outline'
import Cookies from 'js-cookie';
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css'
import CategoryTabs from './categoryTabs.jsx'
import Products from './products.jsx'

import Cards from './Cards.jsx';
import Footer from '../components/marketing/sections/footers/4_column_with_company_mission.jsx';
import Header from './header/index.jsx';
import Carusel from './carusel.jsx';

function classNames(...classes) {
  return classes.filter(Boolean).join(' ')
  }

function HomePage() {
  const [products, setProducts] = useState(null);
  const [products_1, setProducts_1] = useState(null);
  

  useEffect(() => {
    const apiUrl = 'http://127.0.0.1:8000/api/products/?category_id=1';
  
  
    fetch(apiUrl)
        .then((response) => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then((data) => {
            setProducts(data);
        })
        .catch((error) => {
            console.error('Error fetching product:', error);
        });
  }, [window.location.search]);

  const navigate = useNavigate();

  
  const handleCategoryClick = (categoryId) => {
    const category_id = categoryId != null ? categoryId : ''; // If categoryId is null, use an empty string
    navigate(`/products?data=${encodeURIComponent(JSON.stringify({ category_id }))}`);
};

  
  useEffect(() => {
    const apiUrl = 'http://127.0.0.1:8000/api/products/?category_id=2';
  
  
    fetch(apiUrl)
        .then((response) => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then((data) => {
            setProducts_1(data);
        })
        .catch((error) => {
            console.error('Error fetching product:', error);
        });
  }, [window.location.search]);

  return (
    <>
        <Header/>
          <Carusel/>
              
        <div className="mx-auto max-w-2xl px-4 pt-7 pb-2 sm:px-6 sm:px-4 sm:pt-7 lg:max-w-7xl lg:px-2">
          <div className="flex justify-between space-x-4 items-center">
            <h2 className={classNames("text-3xl font-medium text-gray-900", !products && "animate-pulse bg-gray-200 px-14 py-5 rounded-lg" )}>{products && products[0].category.name}</h2>
            <a href="#" className="whitespace-nowrap text-sm font-medium text-indigo-600 hover:text-indigo-500">
              View all
              <span aria-hidden="true"> &rarr;</span>
            </a>
          </div>
        </div>
          
          <Products products={products} />

          <div className="mx-auto max-w-2xl px-4 pt-7 pb-2 sm:px-6 sm:px-4 sm:pt-7 lg:max-w-7xl lg:px-2">
          <div className="flex justify-between space-x-4 items-center">
            <h2 className={classNames("text-3xl font-medium text-gray-900", !products_1 && "animate-pulse bg-gray-200 px-14 py-5 rounded-lg" )}>{products_1 && products_1[0].category.name}</h2>
            <a href="#" className="whitespace-nowrap text-sm font-medium text-indigo-600 hover:text-indigo-500">
              View all
              <span aria-hidden="true"> &rarr;</span>
            </a>
          </div>
        </div>
          
          <Products products={products_1} />

          <Carusel/>

          

          <Routes>
              <Route index element={<Cards/>} />
          </Routes>
        <Footer/>
    </>
  );
}

export default HomePage;
