// HomePage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import ProductsService from './productsService';
import Header from './header/index.jsx';
import Carusel from './carusel.jsx';
import Products from './products.jsx';
import Footer from '../components/marketing/sections/footers/4_column_with_company_mission.jsx';



function HomePage() {
  const [productsService1, setProductsService1] = useState(new ProductsService(1));
  const [productsService2, setProductsService2] = useState(new ProductsService(2));

  const navigate = useNavigate();

  useEffect(() => {
    productsService1.fetchProducts();
  }, [productsService1]);

  useEffect(() => {
    productsService2.fetchProducts();
  }, [productsService2]);

  const handleCategoryClick = (categoryId) => {
    const category_id = categoryId != null ? categoryId : ''; // If categoryId is null, use an empty string
    navigate(`/products?data=${encodeURIComponent(JSON.stringify({ category_id }))}`);
  };

  return (
    <>
      <Header />
      <Carusel />

      {productsService1.getProducts() && (
        <div className="mx-auto max-w-2xl px-4 pt-7 pb-3 sm:pt-7 lg:max-w-7xl lg:px-3">
          {/* Render your component with productsService1.getProducts() */}
        </div>
      )}

      <Products products={productsService1.getProducts()} />

      {productsService2.getProducts() && (
        <div className="mx-auto max-w-2xl px-4 pt-7 pb-3 sm:pt-7 lg:max-w-7xl lg:px-3">
          {/* Render your component with productsService2.getProducts() */}
        </div>
      )}

      <Products products={productsService2.getProducts()} />
      <Footer />
    </>
  );
}

export default HomePage;
