// HomePage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './header/index.jsx';
import Carusel from './carusel.jsx';
// import ProductList from './ProductList';
import Footer from '../components/marketing/sections/footers/4_column_with_company_mission.jsx';

function HomePage() {
  const [products, setProducts] = useState(null);
  const [products_1, setProducts_1] = useState(null);

  useEffect(() => {
    const fetchData = async (category_id, setProductsCallback) => {
      const apiUrl = `http://127.0.0.1:8000/api/products/?category_id=${category_id}`;

      try {
        const response = await fetch(apiUrl);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const data = await response.json();
        setProductsCallback(data);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    fetchData(1, setProducts);
    fetchData(2, setProducts_1);
  }, [window.location.search]);

  const navigate = useNavigate();

  const handleCategoryClick = (categoryId) => {
    const category_id = categoryId != null ? categoryId : '';
    navigate(`/products?data=${encodeURIComponent(JSON.stringify({ category_id }))}`);
  };

  return (
    <>
      <Header />
      <Carusel />

      <ProductList categoryTitle={products && products[0].category.name} products={products} />

      <ProductList categoryTitle={products_1 && products_1[0].category.name} products={products_1} />

      <Footer />
    </>
  );
}

export default HomePage;
