import { Routes, Route, useNavigate } from 'react-router-dom';
import React, {Fragment, useState, useEffect} from 'react'
import {Dialog, Disclosure, Menu, Transition} from '@headlessui/react'
import {XMarkIcon} from '@heroicons/react/24/outline'
import Cookies from 'js-cookie';
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css'
import CategoryTabs from './categoryTabs.jsx'
import Products from './products.jsx'

import Cards from './Cards.jsx';
import Footer from '../components/marketing/sections/footers/4_column_with_company_mission.jsx';
import Header from './header/index.jsx';
import Carusel from './carusel.jsx';

function HomePage() {
  

  useEffect(() => {
    const apiUrl = decodedData != null
    ? 
      decodedData.category_id ? `http://127.0.0.1:8000/api/products/?category_id=${decodedData.category_id}`
      : `http://127.0.0.1:8000/api/products/?category_id=${decodedData.parent_category_id}`
        : 'http://127.0.0.1:8000/api/products/';
  
  
    fetch(apiUrl)
        .then((response) => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then((data) => {
            setProducts(data);
            setLoading(false);
        })
        .catch((error) => {
            console.error('Error fetching product:', error);
            setLoading(false);
        });
  }, [window.location.search]);

  return (
    <>
        <Header/>
          <Carusel/>
              
          <Products products={products} />

          <Routes>
              <Route index element={<Cards/>} />
          </Routes>

          <Carusel/>

          <Routes>
              <Route index element={<Cards/>} />
          </Routes>
        <Footer/>
    </>
  );
}

export default HomePage;
