import React from 'react';
import Slider from 'react-slick';

import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const Carousel = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    pauseOnHover: true,
  };

  const banners = [
    {
      image: 'https://placekitten.com/800/400', // Replace with your image URL
      title: 'Banner 1',
    },
    {
      image: 'https://placekitten.com/800/401', // Replace with your image URL
      title: 'Banner 2',
    },
    {
      image: 'https://placekitten.com/800/402', // Replace with your image URL
      title: 'Banner 3',
    },
  ];

  return (
    <Slider {...settings} className="mx-auto max-w-7xl px-3 mt-4 overflow-hidden">
      {banners.map((banner, index) => (
        <div key={index} className="relative h sm:h-40 overflow-hidden rounded-lg">
          <div
            className="absolute inset-0 bg-cover bg-center rounded-lg"
            style={{ backgroundImage: `url(${banner.image})` }}
          />
          <div className="absolute inset-0 flex items-center justify-center text-white text-2xl font-bold">
            {banner.title}
          </div>
        </div>
      ))}
    </Slider>
  );
};

export default Carousel;
