import { Link} from 'react-router-dom'
import React from 'react'
import {StarIcon} from '@heroicons/react/20/solid'
import Modal from './test.jsx'
import Skeleton from 'react-loading-skeleton'

function classNames(...classes) {
return classes.filter(Boolean).join(' ')
}


function formatPrice(price) {
if (price == null) return 0
const priceString = price.toString();
const parts = priceString.split('.');
const formattedPrice = parts[0].replace(/\B(?=(\d{3})+(?!\d))/g, ' ');
if (parts[1]) {
return `${formattedPrice}.${parts[1]}`;
} else {
return formattedPrice;
}
}


export default function Cards({products}){



return (
<>
    <div className="mx-auto max-w-7xl overflow-hidden sm:px-6 lg:px-0">
        <div className="-mx-px grid grid-cols-2 sm:mx-0 md:grid-cols-3 lg:grid-cols-4">
        {products ? products.map((product) => (product.is_published && product.characteristics[0] != null ? (
        <div key={product.id} className="group p-1 sm:p-2 fade-in">
            <div className="aspect-h-1 aspect-w-1 overflow-hidden rounded-lg bg-gray-200
                            ">
                <Link to={"/product/" + product.id}>
                    {
                <img src={product.characteristics[0].images[0].middle} className="w-full h-60 object-cover bg-center" />}
                </Link>
            </div>
            <div className="pb-4 pt-5">
                <h3 className="text-base text-gray-900">
                    <Link to={"/product/" + product.id}>
                    {product.name}
                    <span className="placeholder col-6"></span>
                    </Link>
                </h3>

                <h4 className="text-sm text-blue-800">
                    <Link to={"/seller/" + product.seller.id}>
                    {product.seller.store_name}
                    </Link>
                </h4>
                <div className="flex items-center mt-2">
                    <StarIcon className={'text-yellow-400 h-4 w-4 flex-shrink-0'} aria-hidden="true" />
                    <p className="ml-1 text-sm text-gray-500">{product.rating} out
                        of 5 stars</p>
                </div>

                <div className="mt-2">
                    <div className="flex flex-wrap items-center justify-between">
                        <div>
                            {product.characteristics[0].discount_price != 0 ? (
                            <>
                                <del className="mt-1 text-sm text-gray-400 ">{formatPrice(product.characteristics[0].price)}
                                    сум
                                </del>
                                <h4 className="text-lg text-lg font-bold text-gray-700 ">
                                    {formatPrice(product.characteristics[0].discount_price)} сум</h4>
                            </>
                            ) : (
                            <h4 className="text-lg text-lg font-bold text-gray-700 ">
                                {formatPrice(product.characteristics[0].price)} сум</h4>
                            )}
                        </div>
                        <div className="ml-4 flex-shrink-0">
                            <Modal product_id={product.id} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
        ) : (
        <></>
        )
        )
        ) : (
            [1, 2, 3, 4].map(() => (
                <div className="aspect-h-1 m-3 aspect-w-1 overflow-hidden rounded-lg bg-gray-200 group-hover:opacity-90">
                    <Skeleton className="h-80 w-52"/>
                </div>
            ))
        )}
        </div>
    </div>
</>
)
}
